mapper.select:
SELECT p.*,s.name AS statusName,bu.name as attnName,e.organizationName AS companyName,pt.productTypeName AS varietyName, d.`id` AS d_id,d.`IDCard` AS d_idCard,d.`name` AS d_name FROM pledge p INNER JOIN pledgeStatus s ON s.id=p.status INNER JOIN extension e ON e.id=p.company INNER JOIN producttype pt ON pt.id=p.variety inner join backenduser bu on bu.id=p.attn INNER JOIN devuser d ON d.`id`=p.`duId` WHERE 1=1
AND DATE_FORMAT(v.createDate,'%Y-%m-%d') =#{createDate}
and d.name like concat('%',#{keyword},'%')
limit #{page},#{size}

SELECT count(*) FROM pledge p INNER JOIN pledgeStatus s ON s.id=p.status INNER JOIN extension e ON e.id=p.company INNER JOIN producttype pt ON pt.id=p.variety inner join backenduser bu on bu.id=p.attn INNER JOIN devuser d ON d.`id`=p.`duId` WHERE 1=1
AND DATE_FORMAT(v.createDate,'%Y-%m-%d') =#{createDate}
and d.name like concat('%',#{keyword},'%')
