mapper.select:
SELECT v.*,m.`name` AS typeName,d.`id` AS d_id,d.`IDCard` AS d_idCard,d.`name` AS d_name FROM visit v INNER JOIN devuser d ON d.`id`=v.`duId` INNER JOIN visitmethod m ON m.`id`=v.`visitMethodId` WHERE 1=1
AND DATE_FORMAT(v.createDate,'%Y-%m-%d') =#{createDate}
and d.name like concat('%',#{keyword},'%')
limit #{page},#{size}

SELECT count(*) FROM visit v INNER JOIN devuser d ON d.`id`=v.`duId` INNER JOIN visitmethod m ON m.`id`=v.`visitMethodId` WHERE 1=1
AND DATE_FORMAT(v.createDate,'%Y-%m-%d') =#{createDate}
and d.name like concat('%',#{keyword},'%')
